DROP TABLE IF EXISTS `acct_info`;
CREATE TABLE `acct_info`  (
  `acct_code` varchar(32) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '账户编号',
  `acct_name` varchar(128) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '账户名称',
  `login_account` varchar(36) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '客户登录账号,关联cust_info表',
  `acct_type_code` varchar(4) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '0001' COMMENT '账户类型 关联acct_accttype_dict表',
  `avail_bal` decimal(17, 2) NOT NULL DEFAULT 0.00 COMMENT '可用金额',
  `stat` varchar(2) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '00' COMMENT '状态：00可用 01冻结 02销户',
  `frozen_date` datetime(0) NULL DEFAULT NULL COMMENT '冻结日期',
  `bal_limit` decimal(17, 2) NOT NULL DEFAULT 999999999.00 COMMENT '余额限额',
  `create_time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `update_time` timestamp(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  PRIMARY KEY (`acct_code`) USING BTREE,
  INDEX `acct_name_index`(`acct_name`) USING BTREE,
  INDEX `login_account_index`(`login_account`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_bin COMMENT = '账户余额信息' ROW_FORMAT = Dynamic;

