  `sort_weight` int(8) NULL DEFAULT NULL COMMENT '排序权重',
  `time_start` datetime(0) NULL DEFAULT NULL COMMENT '开始时间',
  `time_end` datetime(0) NULL DEFAULT NULL COMMENT '结束时间',
  `status` int(1) NULL DEFAULT 0 COMMENT '投放状态(0:待生效,1:已生效,2:已下架)',
  `remarks` varchar(400) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT '' COMMENT '备注',
  `ad_module_id` varchar(48) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '广告模块编码',
  `operating_time` datetime(0) NULL DEFAULT NULL COMMENT '操作时间',
  PRIMARY KEY (`ad_content_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_bin COMMENT = '广告内容列表' ROW_FORMAT = Dynamic;

